import React, { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import { readDeck, listCards, readCard } from "../utils/api/index";
import BreadCrumbNav from "./BreadCrumbNav";

function Study() {
    const { deckId } = useParams();
    const [studyDeck, setStudyDeck] = useState([]);
    const [studyCard, setStudyCard] = useState([]);
    const [cards, setCards] = useState([]);
    const [cardId, setCardId] = useState(1);
    const [cardNum, setCardNum] = useState(0);
    const [cardTotal, setCardTotal] = useState(1);

    useEffect(() => {
        const fetchDeck = async () => setStudyDeck(await readDeck(deckId));
        fetchDeck();
    }, []);

    return (
        <>
            <BreadCrumbNav link={`/decks/${deckId}`} linkName={studyDeck.name} pageName={'Study'} />
            <div className="row">
                <h1>Study: {studyDeck.name}</h1>
            </div>
            <div className="row d-flex justify-content-center mt-1 w-100">
                <div className="card m-1 p-5">
                    <div className="card-body row text-center">
                        <p className="card-text col-12">{studyDeck.cards.front}</p>
                    </div>
                </div>
                <div className="col-12">
                    <h4 className="card-text d-flex justify-content-end">Card {cardNum} of {cardTotal}</h4>
                </div>
                <div className="d-flex justify-content-center mt-1 btn btn-secondary mb-3">
                    Flip
                </div>
            </div>
        </>
    );
}

export default Study;
