import React, { useState } from "react";
import { Switch, Route } from "react-router-dom";
import Header from "./Header";
import Home from "./Home";
import Deck from "./Deck";
import NewDeck from "./NewDeck";
import EditDeck from "./EditDeck";
import AddCard from "./AddCard";
import EditCard from "./EditCard";
import Study from "./Study";
import NotFound from "./NotFound";

function Layout() {
  const [decks, setDecks] = useState([]);
  const [cards, setCards] = useState([]);

  useEffect(() => {
    const fetchDecks = async () => setDecks(await listDecks())
    fetchDecks();
  }, []);

  return (
    <>
      <Header />
      <div className="container">
        <Switch>
          <Route path="/" exact={true}>
            <Home decks={decks} setDecks={setDecks} cards={cards} setCards={setCards} />
          </Route>
          <Route path="/decks/:deckId/cards/:cardId/edit">
            <EditCard />
          </Route>
          <Route path="/decks/:deckId/cards/new">
            <AddCard />
          </Route>
          <Route path="/decks/:deckId/study">
            <Study decks={decks} cards={cards} />
          </Route>
          <Route path="/decks/new">
            <NewDeck decks={decks} setDecks={setDecks} />
          </Route>
          <Route path="/decks/:deckId/edit">
            <EditDeck />
          </Route>
          <Route path="/decks/:deckId">
            <Deck decks={decks} cards={cards} />
          </Route>
          <Route> {/* 404 */}
            <NotFound />
          </Route>
        </Switch>

      </div>
    </>
  );
}

export default Layout;
