import React, { useState, useEffect } from "react";
import { Switch, Route, Link } from "react-router-dom";
import {EyeIcon } from '@primer/octicons-react';

function Decks({ decks }) {
  const [cards, setCards] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5000/cards")
      .then(res => res.json())
      .then(res => setCards(res));
  }, []);

  {/* Existing decks are shown with the deck name, number of cards, and a study view and delete button */ }
  const content = decks.map(deck => {
    const numberOfCards = cards.filter(card => card.deckId === deck.id).length;
    return (<div className="row text-center d-flex justify-content-center mt-1">
      <div className="card w-100">
        <div className="card-body">
          <div className="d-flex justify-content-between">
            <h5 className="card-title">{deck.name}</h5>
            <h6 className="text-info">{numberOfCards} cards</h6>
          </div>
          <p className="card-text">{deck.description}</p>
          {/* Clicking the View button brings the user to the Edit Deck screen */}
          <Link to={`decks/${deck.id}/edit`}>
            <button className="btn btn-secondary mr-1" aria-label="Left Align">
              <EyeIcon size={16} /> View
            </button>
          </Link>

          {/* Clicking the Study button brings the user to the Study screen */}
          <Link to={`decks/${deck.id}/study`}>
            <button className="btn btn-secondary" aria-label="Left Align">
               Study
            </button>
          </Link>
          
          {/* Clicking the Delete button shows a warning message before deleting the deck */}
          
        </div>
      </div>
    </div>
    );
  })


  return (
    <>
      {content}
    </>
  );
}

export default Decks;
