import React, { useState, useEffect } from "react";
import BreadCrumbNav from "../Common/BreadCrumbNav";
import StudyCard from "./StudyCard";
import StudyNotEnoughCards from "./StudyNotEnoughCards";

function Study({ deckId }) {
    const [cardNum, setCardNum] = useState(1);
    const [cardText, setCardText] = useState("front");
    const [cardTotal, setCardTotal] = useState(0);
    const [onBack, setOnBack] = useState(false);
    const [onEnd, setOnEnd] = useState(false);
    const [deck, setDeck] = useState([{}]);

    const flip = (e) => {
        if (cardText === cards[cardNum - 1].front) {
            setCardText(text => text = cards[cardNum - 1].back)
            if (cardNum !== cardTotal)
                setOnBack(bool => bool = true);
            else
                setOnEnd(bool => bool = true);
        }
        else {
            setCardText(text => text = cards[cardNum - 1].front)
        }
    }

    const next = (e) => {
        setCardText(text => text = cards[cardNum].front);
        setCardNum(num => num < cards.length ? num + 1 : num + 0);
        setOnBack(bool => bool = false);
    }

    const reset = (e) => {
        setCardText(text => text = cards[0].front);
        setCardNum(num => num = 1);
        setOnBack(bool => bool = false);
        setOnEnd(bool => bool = false);
    }

    useEffect(() => {
        const abortController = new AbortController();
        const fetchDeck = async () => setDeck(await readDeck(deckId, abortController.signal));
        fetchDeck()

        return () => abortController.abort();
    }, [deckId]);

    useEffect(() => {
        const abortController = new AbortController();
        setCardTotal(cards.length);
        setCardText(cards[cardNum - 1].front);
        return () => abortController.abort();
    }, [cards, cardNum]);

    return (
        <>
            <BreadCrumbNav link={`/decks/${deckId}`} linkName={deck.name} pageName={'Study'} />
            <div className="row">
                <h1>Study: {deck.name}</h1>
            </div>
            { cardTotal > 2 ? <StudyCard cardText={cardText} onBack={onBack} onEnd={onEnd} cardNum={cardNum} setCardNum={setCardNum} cardTotal={cardTotal} next={next} flip={flip} reset={reset} /> : <StudyNotEnoughCards cardLength={cardTotal} /> }
        </>
    );
}

export default Study;
