import React, { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import { readDeck, listCards, readCard } from "../utils/api/index";
import BreadCrumbNav from "./BreadCrumbNav";


function Study() {
    const { deckId } = useParams();
    const [studyDeck, setStudyDeck] = useState([]);
    const [studyCard, setStudyCard] = useState([]);
    const [studyCards, setStudyCards] = useState([]);

    useEffect(() => {
        const fetchDeck = async () => setStudyDeck(await readDeck(deckId));
        fetchDeck();
        const fetchCard = async () => setStudyCard(await readCard(deckId));
        fetchCard();
        const fetchCards = async () => setStudyCards(await listCards(deckId));
        fetchCards();
    }, []);

    return (
        <>
            <BreadCrumbNav link={`/decks/${deckId}`} linkName={studyDeck.name} pageName={'Study'} />
            <div className="row">
                <h2>Study: {studyDeck.name}</h2>
            </div>
            <div className="row text-center d-flex justify-content-center mt-1">
                <div className="card w-100">
                    <div className="card-body row">
                        <p className="card-text col-12">{studyCard.front}</p>
                    </div>
                </div>
            </div>
        </>
    );
}

export default Study;
