import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import { EyeIcon, BookIcon, TrashIcon } from '@primer/octicons-react';

function Cards({ deck, cards=[], index, handleDeleteDeck }) {

    return (<div key={index} className="row text-center d-flex justify-content-center mt-1">
        <div className="card w-100">
            <div className="card-body">
                <p className="card-text col-6">FRONT</p>
                <p className="card-text col-6">BACK</p>
                {/* Clicking the View button brings the user to the Edit Deck screen */}
                <div className="d-flex justify-content-between">
                    <div>
                        <Link to={`decks/${deck.id}`}>
                            <button className="btn btn-secondary mr-1" aria-label="Left Align">
                                <EyeIcon size={22} /> View
                </button>
                        </Link>

                        {/* Clicking the Study button brings the user to the Study screen */}
                        <Link to={`decks/${deck.id}/study`}>
                            <button className="btn btn-primary">
                                <BookIcon size={22} /> Study
                </button>
                        </Link>
                    </div>

                    {/* Clicking the Delete button shows a warning message before deleting the deck */}
                    <button data-index={deck.id} className="btn btn-danger" onClick={handleDeleteDeck}>
                        <TrashIcon size={22} /> Delete
            </button>
                </div>
            </div>
        </div>
    </div>
    );
}

export default DeckCard;
