import React, {useState,useEffect} from "react";
import { Switch, Route, Link } from "react-router-dom";
import {PlusIcon} from '@primer/octicons-react';
import Decks from "./Decks";

function Home() {
   const [decks, setDecks] = useState([]);

   useEffect(()=>{
        fetch("http://localhost:5000/decks")
        .then(res => res.json())
        .then(res => setDecks(res));
    },[]);

  return (
    <>
      <div className="row">
        {/* Create Deck Button brings the user to the Create Deck Screen */}
        <Link to="/decks/new"> 
        <button className="btn btn-secondary" aria-label="Left Align">
            <PlusIcon aria-label="Add new item" /> Create Deck
        </button> 
        </Link>
        </div>
        {/* Existing decks are shown with the deck name, number of cards, and a study view and delete button */}
        <Decks decks={decks} />

            {/* Clicking the Study button brings the user to the Study screen */}
            {/* Clicking the Edit button brings the user to the Edit Deck screen */}
            {/* Clicking the Delete button shows a warning message before deleting the deck */}

      
    </>
  );
}

export default Home;
