import React, { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import { readDeck } from "../utils/api/index";
import BreadCrumbNav from "./BreadCrumbNav";


function Study() {
    const { deckId } = useParams();
    const [studyDeck, setStudyDeck] = useState([]);

    useEffect(() => {
        const fetchDeck = async () => setStudyDeck(await readDeck(deckId));
        fetchDeck();
    }, []);

    return (
        <>
            <BreadCrumbNav link={`/decks/${deckId}`} linkName={studyDeck.name} pageName={'Study'} />
            <div className="row">
                <h2>Study: {studyDeck.name}</h2>
            </div>
            <div key={index} className="row text-center d-flex justify-content-center mt-1">
                <div className="card w-100">
                    <div className="card-body row">
                        <p className="card-text col-12">{card.front}</p>
                    </div>
                    {/* Clicking the View button brings the user to the Edit Deck screen */}
                    <div className="d-flex justify-content-end">
                        <div>
                            <Link to={`/decks/${card.deckId}/cards/${card.id}/edit`}>
                                <button className="btn btn-secondary mr-1">
                                    <PencilIcon size={22} /> Edit
                                    </button>
                            </Link>

                            {/* Clicking the Delete button shows a warning message before deleting the deck */}
                            <button data-index={card.id} className="btn btn-danger" onClick={handleDeleteCard}>
                                Delete
                        </button>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}

export default Study;
