import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import { listDecks } from "../utils/api/index";
import { PlusIcon } from '@primer/octicons-react';
import Decks from "./Decks";

function Home({decks, setDecks, cards, setCards}) {
    return (
        <>
            <div className="row">
                {/* Create Deck Button brings the user to the Create Deck Screen */}
                <Link to="/decks/new">
                    <button className="btn btn-secondary">
                        <PlusIcon /> Create Deck
                    </button>
                </Link>
            </div>
            <Decks decks={decks} cards={cards} setDecks={setDecks} cards={cards} setCards={setCards} />
        </>
    );
}

export default Home;
