import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { HomeFillIcon } from '@primer/octicons-react';

function NewDeck() {
    return (
        <>
            <div className="row bg-light">
                <Link className="m-1" to="/"><HomeFillIcon size={24} />Home</Link>
                <p className="m-1">/ Create Deck</p>
            </div>
            <div className="row">
                <h2>Create Deck</h2>
            </div>
            <form className="row">
                <div className="form-group w-100">
                    <label htmlFor="deck-name">Name</label>
                    <input type="text" required className="form-control" id="deck-name" name="deck-name" placeholder="Deck Name" />
                </div>
                <div className="form-group w-100">
                    <label htmlFor="deck-description">Description</label>
                    <textarea type="text" required className="form-control" id="deck-name" name="deck-name" placeholder="Brief description of the deck" />
                </div>
                <button className="btn btn-primary">Cancel</button>
                <button className="btn btn-primary" type="submit">Submit</button>

            </form>


        </>
    );
}

export default NewDeck;
