import React, { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import { deleteDeck, listDecks } from "../utils/api/index";
import { HomeFillIcon, BookIcon, TrashIcon } from '@primer/octicons-react';

function Deck({ decks }) {
    const { deckId } = useParams();

    const [deck, setDeck] = useState([]);
    const [deckName, setDeckName] = useState("Deck Name");

    let content = "";

    const handleDeleteDeck = async (e) => {
        if (window.confirm('Delete this deck?'))
            await deleteDeck(e.target.getAttribute("data-index"));
    }

    useEffect(() => {
        setDeck(currentDeck => currentDeck = decks.filter(deck => {
            if (Number(deck.id) === Number(deckId)) {
                setDeckName(name => name = deck.name)
                content = (<div className="row text-center d-flex justify-content-center mt-1">
                    <div className="card w-100">
                        <div className="card-body">
                            <div className="d-flex justify-content-between">
                                <h5 className="card-title">{deck.name}</h5>
                            </div>
                            <p className="card-text">{deck.description}</p>

                            {/* Clicking the View button brings the user to the Edit Deck screen */}
                            <div className="d-flex justify-content-between">
                                <div>
                                    <Link to={`decks/${deck.id}`}>
                                        <button className="btn btn-secondary mr-1" aria-label="Left Align">
                                            Edit
                </button>
                                    </Link>

                                    {/* Clicking the Study button brings the user to the Study screen */}
                                    <Link to={`decks/${deck.id}/study`}>
                                        <button className="btn btn-primary" aria-label="Left Align">
                                            <BookIcon size={16} /> Study
                </button>
                                    </Link>
                                </div>

                                {/* Clicking the Delete button shows a warning message before deleting the deck */}
                                <button data-index={deck.id} className="btn btn-danger" aria-label="Left Align" onClick={handleDeleteDeck}>
                                    <TrashIcon size={16} /> Delete
            </button>
                            </div>
                        </div>
                    </div>
                </div>)
                return deck;
            }
        }))
    }, []);

    return (
        <>
            <div className="row bg-light">
                <Link className="m-1" to="/"><HomeFillIcon size={24} />Home</Link>
                <p className="m-1">/ {deckName}</p>
            </div>
        </>
    );
}
export default Deck;
