import React, { useState, useEffect } from "react";
import { Switch, Route, useParams, useHistory } from "react-router-dom";
import { deleteDeck, readDeck, listCards } from "../utils/api/index";
import Cards from "./Cards";
import DeckInfoCard from "./DeckInfoCard";
import Study from "./Study";
import EditDeck from "./EditDeck";

function Deck() {
    const { deckId } = useParams();
    const history = useHistory();

    const [deck, setDeck] = useState([{}]);
    const [cards, setCards] = useState([{}]);

    useEffect(() => {
        const abortController = new AbortController()
        
        const fetchCards = async () => setCards(await listCards(deckId, abortController.signal))
        fetchCards();

        return () => abortController.abort()
    }, [deckId]);

    const handleDeleteDeck = async (e) => {
        if (window.confirm('Delete this deck?')) {
            await deleteDeck(e.target.getAttribute("data-index"));
            history.push("/")
        }
    }

    useEffect(() => {
        const fetchDeck = async () => setDeck(await readDeck(deckId));
        fetchDeck();

        const fetchCards = async () => setCards(await listCards(deckId));
        fetchCards();
    }, [deckId]);

    return (
        <>
            <Switch>
                <Route path="/decks/:deckId/study">
                    <Study deck={deck} cards={cards} deckId={deckId} />
                </Route>
                <Route path="/decks/:deckId/edit">
                    <EditDeck />
                </Route>
                <Route path="/decks/:deckId">
                    <DeckInfoCard deck={deck} deckId={deckId} handleDeleteDeck={handleDeleteDeck} />
                    <Cards cards={cards} setCards={setCards} />
                </Route>
            </Switch>
        </>
    );
}
export default Deck;
