import React, { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import { listDecks } from "../utils/api/index";
import { HomeFillIcon } from '@primer/octicons-react';
import DeckCard from "./DeckCard";

function Study({ decks, cards }) {
        const { deckId } = useParams();

        const [studyDeck, setStudyDeck] = useState([]);
        const [studyName, setStudyName] = useState("Deck Name");


        useEffect(() => {
            setStudyDeck(deck => deck = decks.filter(d => {
                if (Number(d.id) === Number(deckId)) {
                    setStudyName(name => name = d.name)
                    return d;
                }
            }))
        }, []);


        return (
            <>
                <div className="row bg-light">
                    <Link className="m-1" to="/"><HomeFillIcon size={24} />Home</Link>
                    <p className="m-1">/ <Link to={`/decks/${deckId}`}>{studyName}</Link> / Study</p>
                </div>
                <div className="row">
                    <h2>Study: {studyName}</h2>
                </div>
                <DeckCard deck={studyDeck} cards={cards} />
            </>
        );
    }

export default Study;
