import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { deleteDeck, listDecks } from "../utils/api/index";
import { EyeIcon, BookIcon, TrashIcon } from '@primer/octicons-react';

function Decks({ decks, setDecks, cards, setCards }) {
  const handleDeleteDeck = (e) => {
    if (window.confirm('Delete this deck?'))
      deleteDeck(e.target.getAttribute("data-index"));
    useEffect(() => {
      const fetchDecks = async () => setDecks(await listDecks())
      fetchDecks();
    }, []);
  }

  useEffect(() => {
    fetch("http://localhost:5000/cards")
      .then(res => res.json())
      .then(res => setCards(res));
  }, []);

  {/* Existing decks are shown with the deck name, number of cards, and a study view and delete button */ }
  const content = decks.map((deck, index) => {
    const numberOfCards = cards.filter(card => card.deckId === deck.id).length;
    return (<div key={index} className="row text-center d-flex justify-content-center mt-1">
      <div className="card w-100">
        <div className="card-body">
          <div className="d-flex justify-content-between">
            <h5 className="card-title">{deck.name}</h5>
            <Link to={`/decks/${deck.id}`}><h6 className="text-info">{numberOfCards} cards</h6></Link>
          </div>
          <p className="card-text">{deck.description}</p>

          {/* Clicking the View button brings the user to the Edit Deck screen */}
          <div className="d-flex justify-content-between">
            <div>
              <Link to={`decks/${deck.id}`}>
                <button className="btn btn-secondary mr-1" aria-label="Left Align">
                  <EyeIcon size={16} /> View
                </button>
              </Link>

              {/* Clicking the Study button brings the user to the Study screen */}
              <Link to={`decks/${deck.id}/study`}>
                <button className="btn btn-primary" aria-label="Left Align">
                  <BookIcon size={16} /> Study
                </button>
              </Link>
            </div>

            {/* Clicking the Delete button shows a warning message before deleting the deck */}
            <button data-index={deck.id} className="btn btn-danger" aria-label="Left Align" onClick={handleDeleteDeck}>
              <TrashIcon size={16} /> Delete
            </button>
          </div>
        </div>
      </div>
    </div>
    );
  })

  return (
    <>
      {content}
    </>
  );
}

export default Decks;
