import React, { useState, useEffect } from "react";
import NextButton from "./NextButton";

function StudyCard({ front = "Card Text", back, cardNum = 0, cards = [], setFront, setCardNum }) {
    const [cardText, setCardText] = useState(front);
    const [cardTotal, setCardTotal] = useState(0);
    const flip = (e) => {
        if (cardText === front) {
            setCardText(text => text = back)
            document.getElementById('nextBtn').style.display = "block";
        }
        else {
            setCardText(text => text = front)
        }
    }

    useEffect(() => {
        setCardTotal(cards.length);
        document.getElementById('nextBtn').style.display = "none";
    }, [cards]);

    const next = (e) => {
        setCardNum(num => num < cards.length ? num + 1 : num + 0);
        setCardText(text => text = front)
        document.getElementById('nextBtn').style.display = "none";
    }
    return (
        <>
            <div className="row d-flex justify-content-center mt-1 w-100">
                <div className="card m-1 p-5">
                    <div className="card-body row text-center">
                        <p className="card-text col-12" >{cardText}</p>
                    </div>
                </div>
                <div className="col-12">
                    <h4 className="card-text d-flex justify-content-end">Card {cardNum} of {cardTotal}</h4>
                </div>
                <div className="d-flex justify-content-center mt-1 btn btn-secondary mb-3" onClick={flip}>
                    Flip
                </div>
                {cardText === back ? <NextButton next={next} /> : null}
            </div>
        </>
    );
}

export default StudyCard;
