import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import { listDecks } from "../utils/api/index";
import { PlusIcon } from '@primer/octicons-react';
import Decks from "./Decks";

function Home({decks, setDecks, cards, setCards}) {

    useEffect(() => {
        const fetchDecks = async () => setDecks(await listDecks())
        fetchDecks();
    }, []);

    return (
        <>
            <div className="row">
                {/* Create Deck Button brings the user to the Create Deck Screen */}
                <Link to="/decks/new">
                    <button className="btn btn-secondary" aria-label="Left Align">
                        <PlusIcon aria-label="Add new item" /> Create Deck
                    </button>
                </Link>
            </div>
            <Decks decks={decks} setDecks={setDecks} cards={cards} setCards={setCards} />
        </>
    );
}

export default Home;
