import React, {useState,useEffect} from "react";
import { Switch, Route, Link } from "react-router-dom";

function Decks({decks}) {
  const [cards, setCards] = useState([]);

   useEffect(()=>{
        fetch("http://localhost:5000/cards")
        .then(res => res.json())
        .then(res => setCards(res));
    },[]);

     {/* Existing decks are shown with the deck name, number of cards, and a study view and delete button */}
    const content = decks.map(deck => {
      const numberOfCards = cards.filter(card => card.deckId === deck.id).length;
      return (<div className="row">
        <div className="card">
          <div className="card-body">
            <div className="d-flex">
              <h5 className="card-title justify-content-start">{deck.name}</h5>
              <h6 className="text-info justify-content-end">{numberOfCards} cards</h6>
            </div>
            <p className="card-text">{deck.description}</p>
            {/* Clicking the Study button brings the user to the Study screen */}
            {/* Clicking the Edit button brings the user to the Edit Deck screen */}
            {/* Clicking the Delete button shows a warning message before deleting the deck */} 
          </div>
        </div>
      </div>
      );
    })
  

  return (
    <>
        {content}
    </>
  );
}

export default Decks;
