import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import { HomeFillIcon } from '@primer/octicons-react';

function BreadCrumbNav({ pageName = "Page Name", link = "", linkName = "",  }) {
    const homeNavLink = (<Link className="m-1" to="/"><HomeFillIcon size={24} />Home</Link>);
    const forwardSlash = (<p className="m-1"> /</p>)

    let content = homeNavLink + forwardSlash + (<Link to={link}>{linkName}</Link>) + (link === "" ? pageName : forwardSlash + pageName);

    return (
        <>
            <div className="row bg-light">
                {content}
            </div>
        </>
    );
}

export default BreadCrumbNav;
