import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { createDeck } from "../utils/api/index";
import { HomeFillIcon } from '@primer/octicons-react';

function NewDeck() {
    const [newDeckName, setNewDeckName] = useState("");
    const [newDeckDesc, setNewDeckDesc] = useState("");
    const handleCreateDeck = (e) => {
        e.preventDefault();
        console.log(e.target);
    }
    const handleNameChange = (e) => setNewDeckName(e.target.value);
    const handleDescChange = (e) => setNewDeckDesc(e.target.value);
    return (
        <>
            <div className="row bg-light">
                <Link className="m-1" to="/"><HomeFillIcon size={24} />Home</Link>
                <p className="m-1">/ Create Deck</p>
            </div>
            <div className="row">
                <h2>Create Deck</h2>
            </div>
            <form className="row" onSubmit={handleCreateDeck}>
                <div className="form-group w-100">
                    <label htmlFor="deck-name">Name</label>
                    <input type="text" required className="form-control" onChange={handleNameChange} placeholder="Deck Name" />
                </div>
                <div className="form-group w-100">
                    <label htmlFor="deck-description">Description</label>
                    <textarea type="text" required className="form-control" onChange={handleDescChange} placeholder="Brief description of the deck" />
                </div>
                <Link to="/"><button className="btn btn-secondary mr-2">Cancel</button></Link>
                <button className="btn btn-primary" type="submit">Submit</button>
            </form>
        </>
    );
}

export default NewDeck;
