import React, { useEffect, useState } from "react";
import { PencilIcon } from '@primer/octicons-react';
import { Link, useParams, useHistory } from "react-router-dom";
import { deleteCard, readCard } from "../utils/api/index";

function Card({ index }) {
    const { deckId, cardId } = useParams();
    const history = useHistory();

    const [card, setCard] = useState();

    const handleDeleteCard = async (e) => {
        if (window.confirm('Delete this card?')) {
            await deleteCard(cardId);
        }
    }

    useEffect(() => {
        const fetchCard = async () => setCard(await readCard())
        fetchCard();
    }, []);
    return (<>
        <div key={index} className="row text-center d-flex justify-content-center mt-1">
            <div className="card w-100">
                <div className="card-body row">
                    <p className="card-text col-6">{card.front}</p>
                    <p className="card-text col-6">{card.back}</p>
                </div>
                {/* Clicking the View button brings the user to the Edit Deck screen */}
                <div className="d-flex justify-content-end">
                    <div>
                        <Link to={`/decks/${card.deckId}/cards/${card.id}/edit`}>
                            <button className="btn btn-secondary mr-1">
                                <PencilIcon size={22} /> Edit
                                    </button>
                        </Link>

                        {/* Clicking the Delete button shows a warning message before deleting the deck */}
                        <button data-index={card.id} className="btn btn-danger" onClick={handleDeleteCard}>
                            Delete
                        </button>
                    </div>
                </div>
            </div>
        </div>
    </>);
}

export default Card;
