import React, { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import { HomeFillIcon } from '@primer/octicons-react';

function Deck({decks}) {
    
    const { deckId } = useParams();
    const [deck, setDeck] = useState([]);
    const [deckName, setDeckName] = useState("Deck Name");

    useEffect(() => {
        setDeck(deck => deck = decks.filter(d => {
            if(Number(d.id) === Number(deckId)){
                setDeckName(name => name = d.name)
                return d;
            }
        }))
    }, []);
   
    return (
        <>
            <div className="row bg-light">
                <Link className="m-1" to="/"><HomeFillIcon size={24} />Home</Link>
                <p className="m-1">/ {deckName}</p>
            </div>
            <div className="row">
                <p>... TBD ...</p>
            </div>
        </>
    );
}
export default Deck;
