import React, { useState, useEffect } from "react";
import { Link, useHistory, useParams } from "react-router-dom";
import { readDeck } from "../utils/api/index";
import { HomeFillIcon } from '@primer/octicons-react';
import BreadCrumbNav from "./BreadCrumbNav";
function AddCard() {
    const { deckId } = useParams();
    const [deck, setDeck] = useState("");
    const [deckName, setDeckName] = useState("");
    const [deckDescrip, setDeckDescrip] = useState("");


    const history = useHistory();

    const handleEditDeck = (e) => {
        e.preventDefault();
        console.log('edited');
    }

    useEffect(() => {
        const fetchDeck = async () => setDeck(await readDeck(deckId));
        fetchDeck();
    }, []);

    const handleFrontChange = (e) => setDeckName(e.target.value);
    const handleBackChange = (e) => setDeckDescrip(e.target.value);
    return (
        <>
            <BreadCrumbNav link={`/decks/${deck.id}`} linkName={deck.name} pageName={"Edit Deck"} />
            <div className="row">
                <h4>{deck.name}: Add Card</h4>
            </div>
            <form className="row" onSubmit={handleEditDeck}>
                <div className="form-group w-100">
                    <label htmlFor="deck-name">Name</label>
                    <input type="text" required className="form-control" onChange={handleFrontChange} placeholder={deck.name} />
                </div>
                <div className="form-group w-100">
                    <label htmlFor="deck-description">Description</label>
                    <textarea type="text" required className="form-control" onChange={handleBackChange} placeholder={deck.description} />
                </div>
                <Link to="/"><button className="btn btn-secondary mr-2">Cancel</button></Link>
                <button className="btn btn-primary" type="submit">Submit</button>
            </form>
        </>
    );
}

export default AddCard;
