import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { readDeck, listCards, readCard } from "../utils/api/index";
import BreadCrumbNav from "./BreadCrumbNav";
import StudyCard from "./StudyCard";

function Study() {
    const { deckId } = useParams();
    const [studyDeck, setStudyDeck] = useState([]);
    const [cards, setCards] = useState([{ front: "front" }]);
    const [cardId, setCardId] = useState(0);
    const [cardNum, setCardNum] = useState(1);
    const [front, setFront] = useState("front of card");
    const [back, setBack] = useState("back of card");
    const [cardTotal, setCardTotal] = useState(0);

    useEffect(() => {
        const fetchDeck = async () => setStudyDeck(await readDeck(deckId));
        fetchDeck();

        const fetchCards = async () => setCards(await listCards(deckId));
        fetchCards();
    }, []);

    useEffect(() => {
        load();
    }, [cards]);

    const load = () => {
        setCardTotal(cards.length);
        setFront(text => text = Object.values(cards)[cardNum-1].front);
        setBack(text => text = Object.values(cards)[cardNum-1].back);
    }

    

    return (
        <>
            <BreadCrumbNav link={`/decks/${deckId}`} linkName={studyDeck.name} pageName={'Study'} />
            <div className="row">
                <h1>Study: {studyDeck.name}</h1>
            </div>
            <StudyCard cards={cards} cardNum={cardNum} setCardNum={setCardNum} cardId={cardId} front={Object.values(cards)[cardNum-1].front} setFront={setFront} back={back} cardTotal={cardTotal} setCardTotal={setCardTotal} />
        </>
    );
}

export default Study;
