import React, { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import { listDecks } from "../utils/api/index";
import { HomeFillIcon } from '@primer/octicons-react';

function Study({decks, cards}) {
    const [studyDeck, setStudyDeck] = useState([]);
    const {deckId} = useParams();
    useEffect(() => {
        const fetchDecks = async () => setStudyDeck(await listDecks()).filter(deck => deck.id === deckId)
        fetchDecks();
    }, []);

    console.log(studyDeck);
    return (
        <>
            <div className="row bg-light">
                <Link className="m-1" to="/"><HomeFillIcon size={24} />Home</Link>
                <p className="m-1">/ {studyDeck.name} / Study</p>
            </div>
            <div className="row">
                <p>... TBD ...</p>
            </div>
        </>
    );
}

export default Study;
