import React, { useState, useEffect } from "react";
import { Link, useHistory, useParams } from "react-router-dom";
import { readDeck, updateDeck } from "../utils/api/index";
import { HomeFillIcon } from '@primer/octicons-react';
import BreadCrumbNav from "./BreadCrumbNav";

function EditDeck() {
    const { deckId } = useParams();
    const [deck, setDeck] = useState("");
    


    const history = useHistory();

    const handleEditDeck = (e) => {
        e.preventDefault();
        const updatedDeck = {
            "id": deckId,
            "name": deckName,
            "description": deckDescrip
        };
        updateDeck(updatedDeck);
        history.push(`/decks/${deckId}`);
    }

    useEffect(() => {
        const fetchDeck = async () => setDeck(await readDeck(deckId));
        fetchDeck();
    }, []);

    const [deckName, setDeckName] = useState(deck.name);
    const [deckDescrip, setDeckDescrip] = useState(deck.description);

    const handleNameChange = (e) => setDeckName(e.target.value);
    const handleDescripChange = (e) => setDeckDescrip(e.target.value);
    return (
        <>
            <BreadCrumbNav link={`/decks/${deck.id}`} linkName={deck.name} pageName={"Edit Deck"} />
            <div className="row">
                <h2>Edit Deck</h2>
            </div>
            <form className="row" onSubmit={handleEditDeck}>
                <div className="form-group w-100">
                    <label htmlFor="deck-name">Name</label>
                    <input type="text" required className="form-control" onChange={handleNameChange} defaultValue={deck.name} />
                </div>
                <div className="form-group w-100">
                    <label htmlFor="deck-description">Description</label>
                    <textarea type="text" required className="form-control" onChange={handleDescripChange} defaultValue={deck.description} />
                </div>
                <Link to={`/decks/${deck.id}`}><button className="btn btn-secondary mr-2">Cancel</button></Link>
                <button className="btn btn-primary" type="submit">Submit</button>
            </form>
        </>
    );
}

export default EditDeck;
