import React, { useState, useEffect } from "react";
import { Link, useParams, useHistory } from "react-router-dom";
import { deleteDeck, listDecks } from "../utils/api/index";
import { PencilIcon, BookIcon, TrashIcon } from '@primer/octicons-react';
import BreadCrumbNav from "./BreadCrumbNav";
import Cards from "./Cards";
import "./Styles.css";

function Deck({ decks, cards }) {
    const { deckId } = useParams();
    const history = useHistory();

    const [deck, setDeck] = useState([]);
    const [deckName, setDeckName] = useState("Deck Name");
    const [deckDescrip, setDeckDescrip] = useState("Description");

    const handleDeleteDeck = async (e) => {
        if (window.confirm('Delete this deck?')){
            await deleteDeck(e.target.getAttribute("data-index"));
            history.push("/")
        }
    }

    useEffect(() => {
        setDeck(currentDeck => currentDeck = decks.filter(deck => {
            if (Number(deck.id) === Number(deckId)) {
                setDeckName(name => name = deck.name)
                setDeckDescrip(text => text = deck.description)
                return deck;
            }
        }))
    }, []);
    return (
        <>
            <BreadCrumbNav pageName={deckName} />
            <div className="row text-center d-flex justify-content-center mt-1">
                <div className="w-100">
                    <div>
                        <div className="d-flex justify-content-between">
                            <h4>{deckName}</h4>
                        </div>
                        <p className="card-text">{deckDescrip}</p>

                        {/* Clicking the View button brings the user to the Edit Deck screen */}
                        <div className="d-flex justify-content-between">
                            <div>
                                <Link to={`/decks/${deckId}/edit`}>
                                    <button className="btn btn-secondary mr-1">
                                    <PencilIcon size={22} /> Edit
                                    </button>
                                </Link>

                                {/* Clicking the Study button brings the user to the Study screen */}
                                <Link to={`/decks/${deckId}/study`}>
                                    <button className="btn btn-primary">
                                        <BookIcon size={22} /> Study
                                     </button>
                                </Link>
                            </div>

                            {/* Clicking the Delete button shows a warning message before deleting the deck */}
                            <button data-index={deckId} className="btn btn-danger" onClick={handleDeleteDeck}>
                                <TrashIcon className="remove" size={22} /> Delete
                            </button>
                        </div>
                    </div>
                </div>
            </div>
            {/* Cards */}
            <div className="row mt-3">
                <h3>Cards</h3>
            </div>
        </>
    );
}
export default Deck;
