import React, { useState, useEffect } from "react";
import Card from "./Card";
import { deleteCard, listCards } from "../../utils/api/index";
import { useParams } from "react-router-dom";

function Cards() {
    const { deckId } = useParams();
    const [cards, setCards] = useState([{}]);
    let content = [""];

    useEffect(() => {
        const fetchCards = async () => setCards(await listCards(deckId))
        fetchCards();
    }, [deckId]);

    const handleDeleteCard = async (e) => {
        if (window.confirm('Delete this card?')) {
            await deleteCard(e.target.getAttribute("data-index"));
        }
    }
    if (cards) {
        content = cards.map((card = {}, index = 0) => {
            return (<Card card={card} index={index} handleDeleteCard={handleDeleteCard} />);
        });
    }

    return (<>
        <div className="row mt-3">
            <h3>Cards</h3>
        </div>
        {content}
    </>
    );
}

export default Cards;
