import React, { useState, useEffect } from "react";

function StudyCard({ front, back, cardNum = 0, cards, setFront, setCardNum}) {
    const [cardText, setCardText] = useState("Card Text");
    const flip = (e) => {
        setFront(text => text = back)
    }

    const next = (e) => {
        setCardNum(num => num < cards.length ? num + 1 : num + 0);
    }
    return (
        <>
            <div className="row d-flex justify-content-center mt-1 w-100">
                <div className="card m-1 p-5">
                    <div className="card-body row text-center">
                        <p className="card-text col-12" >{cardText}</p>
                    </div>
                </div>
                <div className="col-12">
                    <h4 className="card-text d-flex justify-content-end">Card {cardNum} of {cards.length}</h4>
                </div>
                <div className="d-flex justify-content-center mt-1 btn btn-secondary mb-3" onClick={flip}>
                    Flip
                </div>
                <div className="d-flex justify-content-center mt-1 btn btn-primary mb-3 ml-2" onClick={next}>
                    Next
                </div>
            </div>
        </>
    );
}

export default StudyCard;
