import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import { HomeFillIcon } from '@primer/octicons-react';

function BreadCrumbNav({ pageName = "Page Name", link = "", linkName = "", }) {
    let content = "";
    if(link === "")
        content = <p className="m-1 text-info">{pageName}</p>
    else
        content = `<Link to={link}>{linkName}</Link>` + <p className="m-1 text-info">{pageName}</p>
    

    return (
        <>
            <div className="row bg-light">
                <Link className="m-1" to="/"><HomeFillIcon size={24} />Home</Link><p className="m-1 text-info"> / </p>
                {content}
            </div>
        </>
    );
}

export default BreadCrumbNav;
